mapper.select:
SELECT b.id, b.category, b.title, u.nickname, b.created_at
FROM board b
JOIN users u ON b.user_id = u.id


        b.category = #{category}


ORDER BY b.created_at DESC
LIMIT #{size} OFFSET #{offset}

mapper.update:
UPDATE board
SET
category = #{category},
title = #{title},
content = #{content}

    , image_url = #{imageUrl}


    , image_url = null

WHERE id = #{id}
